import sailpoint.object.CustomGlobal;

List results = new ArrayList();
results.add("CustomGlobal BEFORE clear had ["+
            CustomGlobal.size() + "] keys.");
CustomGlobal.clear();
results.add("CustomGlobal AFTER clear has [" +
           CustomGlobal.size()+"] keys.");

return results;

Script.Source:
import
sailpoint.object.Certification;
import
sailpoint.object.QueryOptions;
import
sailpoint.object.Filter;

QueryOptions qo
= new QueryOptions();
List names = new
ArrayList();
names.add(currentUser.getName());
qo.addFilter(Filter.containsAll("certifiers",
names));
qo.addFilter(Filter.ne("phase",
Certification.Phase.Staged));
qo.addFilter(Filter.isnull("signed"));

int count =
context.countObjects(Certification.class,
qo);
return count;


import sailpoint.object.WorkItem;
import sailpoint.object.QueryOptions;
import sailpoint.object.Filter;
import java.util.List;
import java.util.ArrayList;

QueryOptions qo = new QueryOptions();
List filters = new ArrayList();

filters.add(Filter.eq("owner", currentUser));

if (currentUser.getWorkgroups() != null && !currentUser.getWorkgroups().isEmpty())
{
  filters.add(Filter.in("owner", currentUser.getWorkgroups()));
}    

qo.addFilter(Filter.eq("type", "Approval"));
qo.addFilter(Filter.or(filters));

int count = context.countObjects(WorkItem.class, qo);
return count;


